  const userRecyclingItems = user ? getUserRecyclingItems(user.id) : [];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const calculateEstimatedValue = () => {
    const selectedMaterial = materialTypes.find(m => m.value === formData.type);
    if (!selectedMaterial || !formData.weight) return 0;
    return selectedMaterial.price * parseFloat(formData.weight);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user || !formData.type || !formData.weight || !formData.quantity) return;

    setIsSubmitting(true);

    try {
      const newItem: RecyclingItem = {
        id: generateId(),
        userId: user.id,
        type: formData.type as RecyclingItem['type'],
        weight: parseFloat(formData.weight),
        quantity: parseInt(formData.quantity),
        estimatedValue: calculateEstimatedValue(),
        status: 'pending',
        date: new Date().toISOString(),
      };

      saveRecyclingItem(newItem);

      setFormData({ type: '', weight: '', quantity: '' });
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    } catch (error) {
      console.error('Error saving recycling item:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-6 lg:py-8">
        {/* Header */}
        <div className="mb-6 lg:mb-8">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900">Nova Reciclagem</h1>
          <p className="text-gray-600 mt-1 text-sm sm:text-base">
            Registre seus materiais recicl√°veis e ganhe cr√©ditos
          </p>
        </div>

        {showSuccess && (
          <div className="mb-6 bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-lg">
            ‚úÖ Reciclagem registrada com sucesso! Aguardando aprova√ß√£o do coletor.
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-8">
          {/* Form */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <div className="flex items-center mb-6">
              <Plus className="h-6 w-6 text-primary-600 mr-3" />
              <h2 className="text-xl font-semibold text-gray-900">Registrar Material</h2>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-2">
                  Tipo de Material
                </label>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                  required
                >
                  <option value="">Selecione o tipo</option>
                  {materialTypes.map((material) => (
                    <option key={material.value} value={material.value}>
                      {material.icon} {material.label} - R$ {material.price.toFixed(2)}/kg
                    </option>
                  ))}
                </select>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="weight" className="block text-sm font-medium text-gray-700 mb-2">
                    <Scale className="h-4 w-4 inline mr-1" />
                    Peso (kg)
                  </label>
                  <input
                    id="weight"
                    name="weight"
                    type="number"
                    step="0.1"
                    min="0"
                    value={formData.weight}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    placeholder="Ex: 2.5"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="quantity" className="block text-sm font-medium text-gray-700 mb-2">
                    <Package className="h-4 w-4 inline mr-1" />
                    Quantidade (unidades)
                  </label>
                  <input
                    id="quantity"
                    name="quantity"
                    type="number"
                    min="1"
                    value={formData.quantity}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                    placeholder="Ex: 10"
                    required
                  />
                </div>
              </div>

              {formData.type && formData.weight && (
                <div className="bg-primary-50 border border-primary-200 rounded-lg p-4">
                  <h4 className="font-semibold text-primary-900 mb-2">Estimativa de Valor</h4>
                  <p className="text-2xl font-bold text-primary-600">
                    R$ {calculateEstimatedValue().toFixed(2)}
                  </p>
                  <p className="text-primary-700 text-sm">
                    Valor sujeito √† aprova√ß√£o do coletor
                  </p>
                </div>
              )}

              <button
                type="submit"
                disabled={isSubmitting || !formData.type || !formData.weight || !formData.quantity}
                className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isSubmitting ? (
                  <div className="flex items-center justify-center">
                    <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Registrando...
                  </div>
                ) : (
                  'Registrar Reciclagem'
                )}
              </button>
            </form>
          </div>

          {/* Collection Points */}
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow-lg p-6">
              <div className="flex items-center mb-6">
                <MapPin className="h-6 w-6 text-blue-600 mr-3" />
                <h3 className="text-xl font-semibold text-gray-900">Pontos de Coleta Pr√≥ximos</h3>
              </div>

              <div className="space-y-4">
                {collectionPoints.map((point) => (
                  <div key={point.id} className="border border-gray-200 rounded-lg p-4 hover:border-primary-300 transition-colors">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h4 className="font-semibold text-gray-900">{point.name}</h4>
                        <p className="text-gray-600 text-sm mt-1">{point.address}</p>
                        <div className="flex items-center text-gray-500 text-sm mt-2">
                          <Clock className="h-4 w-4 mr-1" />
                          {point.hours}
                        </div>
                        <div className="mt-2">
                          <span className="text-xs font-medium text-green-600 bg-green-100 px-2 py-1 rounded">
                            {point.acceptedMaterials.join(', ')}
                          </span>
                        </div>
                      </div>
                      <div className="text-right ml-4">
                        <span className="text-sm font-medium text-gray-900">
                          {point.distance} km
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Recent Submissions */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <div className="flex items-center mb-6">
                <Recycle className="h-6 w-6 text-green-600 mr-3" />
                <h3 className="text-xl font-semibold text-gray-900">√öltimas Reciclagens</h3>
              </div>

              {userRecyclingItems.length === 0 ? (
                <p className="text-gray-500 text-center py-8">
                  Nenhuma reciclagem registrada ainda.
                </p>
              ) : (
                <div className="space-y-3">
                  {userRecyclingItems.slice(0, 5).map((item) => {
                    const material = materialTypes.find(m => m.value === item.type);
                    return (
                      <div key={item.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center">
                          <span className="text-lg mr-3">{material?.icon}</span>
                          <div>
                            <p className="font-medium text-gray-900">
                              {material?.label} - {item.weight}kg
                            </p>
                            <p className="text-sm text-gray-500">
                              {new Date(item.date).toLocaleDateString('pt-BR')}
                            </p>
                          </div>
                        </div>
                        <div className="text-right">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            item.status === 'approved' 
                              ? 'bg-green-100 text-green-800'
                              : item.status === 'rejected'
                              ? 'bg-red-100 text-red-800'
                              : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {item.status === 'approved' ? 'Aprovado' : 
                             item.status === 'rejected' ? 'Rejeitado' : 'Pendente'}
                          </span>
                          <p className="text-sm font-medium text-gray-900 mt-1">
                            R$ {item.estimatedValue.toFixed(2)}
                          </p>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewRecycling;

src/pages/RedeemCredits.tsx.content:
import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { saveUser } from '../utils/localStorage';
import { TransportOption } from '../types';
import { CreditCard, Bus, Train, Navigation, Gift, ArrowRight } from 'lucide-react';

const RedeemCredits: React.FC = () => {
  const { user, updateUser } = useAuth();
  const [selectedTransport, setSelectedTransport] = useState<TransportOption | null>(null);
  const [credits, setCredits] = useState('');
  const [isRedeeming, setIsRedeeming] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const transportOptions: TransportOption[] = [
    {
      id: 'bus',
      type: 'bus',
      name: '√înibus Urbano',
      pricePerCredit: 1.0,
      icon: 'üöå',
    },
    {
      id: 'metro',
      type: 'metro',
      name: 'Metr√¥',
      pricePerCredit: 1.5,
      icon: 'üöá',
    },
    {
      id: 'train',
      type: 'train',
      name: 'Trem',
      pricePerCredit: 2.0,
      icon: 'üöÜ',
    },
  ];

  const calculatePassages = () => {
    if (!selectedTransport || !credits) return 0;
    return Math.floor(parseFloat(credits) / selectedTransport.pricePerCredit);
  };

  const handleRedeem = async () => {
    if (!user || !selectedTransport || !credits) return;

    const creditsToRedeem = parseFloat(credits);
    if (creditsToRedeem > user.credits) return;

    setIsRedeeming(true);

    try {
      const newCredits = user.credits - creditsToRedeem;
      const newTotalRedeemed = user.totalRedeemed + creditsToRedeem;

      const updatedUser = {
        ...user,
        credits: newCredits,
        totalRedeemed: newTotalRedeemed,
      };

      updateUser(updatedUser);
      saveUser(updatedUser);

      setCredits('');
      setSelectedTransport(null);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 3000);
    } catch (error) {
      console.error('Error redeeming credits:', error);
    } finally {
      setIsRedeeming(false);
    }
  };

  if (!user) return null;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Resgatar Cr√©ditos</h1>
          <p className="text-gray-600 mt-1">
            Troque seus cr√©ditos por passagens de transporte p√∫blico
          </p>
        </div>

        {showSuccess && (
          <div className="mb-6 bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-lg">
            ‚úÖ Cr√©ditos resgatados com sucesso! Suas passagens foram creditadas.
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Current Balance */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">Saldo Atual</h2>
                  <div className="text-3xl font-bold text-primary-600">
                    R$ {user.credits.toFixed(2)}
                  </div>
                  <p className="text-gray-600 text-sm mt-1">Dispon√≠vel para resgate</p>
                </div>
                <CreditCard className="h-12 w-12 text-primary-600" />
              </div>
            </div>
          </div>

          {/* Transport Options */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-xl font-semibold text-gray-900 mb-6">Escolha o Transporte</h3>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                {transportOptions.map((option) => (
                  <button
                    key={option.id}
                    onClick={() => setSelectedTransport(option)}
                    className={`p-4 rounded-lg border-2 transition-all ${
                      selectedTransport?.id === option.id
                        ? 'border-primary-500 bg-primary-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <div className="text-center">
                      <div className="text-3xl mb-2">{option.icon}</div>
                      <h4 className="font-semibold text-gray-900 text-sm">{option.name}</h4>
                      <p className="text-gray-600 text-xs mt-1">
                        R$ {option.pricePerCredit.toFixed(2)}/passagem
                      </p>
                    </div>
                  </button>
                ))}
              </div>

              {selectedTransport && (
                <div className="space-y-4">
                  <div>
                    <label htmlFor="credits" className="block text-sm font-medium text-gray-700 mb-2">
                      Valor a resgatar (R$)
                    </label>
                    <input
                      id="credits"
                      type="number"
                      step="0.1"
                      min="0"
                      max={user.credits}
                      value={credits}
                      onChange={(e) => setCredits(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      placeholder={`M√°ximo: R$ ${user.credits.toFixed(2)}`}
                    />
                  </div>

                  {credits && (
                    <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <p className="font-semibold text-blue-900">Voc√™ receber√°:</p>
                          <p className="text-2xl font-bold text-blue-600">
                            {calculatePassages()} passagem{calculatePassages() !== 1 ? 's' : ''}
                          </p>
                          <p className="text-blue-700 text-sm">
                            {selectedTransport.name}
                          </p>
                        </div>
                        <ArrowRight className="h-8 w-8 text-blue-500" />
                      </div>
                    </div>
                  )}

                  <button
                    onClick={handleRedeem}
                    disabled={isRedeeming || !credits || parseFloat(credits) > user.credits || parseFloat(credits) <= 0}
                    className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isRedeeming ? (
                      <div className="flex items-center justify-center">
                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                        Resgatando...
                      </div>
                    ) : (
                      'Resgatar Cr√©ditos'
                    )}
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Resumo da Conta</h3>

              <div className="space-y-4">
                <div className="flex justify-between items-center pb-3 border-b">
                  <span className="text-gray-600">Total Ganho</span>
                  <span className="font-semibold text-green-600">
                    R$ {user.totalEarned.toFixed(2)}
                  </span>
                </div>

                <div className="flex justify-between items-center pb-3 border-b">
                  <span className="text-gray-600">Total Resgatado</span>
                  <span className="font-semibold text-blue-600">
                    R$ {user.totalRedeemed.toFixed(2)}
                  </span>
                </div>

                <div className="flex justify-between items-center">
                  <span className="text-gray-600">Saldo Atual</span>
                  <span className="font-semibold text-primary-600">
                    R$ {user.credits.toFixed(2)}
                  </span>
                </div>
              </div>

              <div className="mt-6 p-4 bg-gray-50 rounded-lg">
                <h4 className="font-semibold text-gray-900 mb-2">üí° Dica</h4>
                <p className="text-gray-600 text-sm">
                  Recicle mais materiais para ganhar mais cr√©ditos e economizar no transporte p√∫blico!
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RedeemCredits;

src/pages/ValidationAdmin.tsx.content:
import React, { useState, useEffect } from 'react';
import { getRecyclingItems, updateRecyclingItem, getUsers, saveUser } from '../utils/localStorage';
import { useAuth } from '../context/AuthContext';
import { RecyclingItem, User } from '../types';
import { CheckCircle, XCircle, Clock, Scale, Package, User as UserIcon } from 'lucide-react';

const ValidationAdmin: React.FC = () => {
  const { user: currentUser, updateUser } = useAuth();
  const [recyclingItems, setRecyclingItems] = useState<RecyclingItem[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [filter, setFilter] = useState<'all' | 'pending' | 'approved' | 'rejected'>('pending');
  const [isProcessing, setIsProcessing] = useState<Record<string, boolean>>({});

  const materialTypes = [
    { value: 'plastic', label: 'Pl√°stico', price: 1.0, icon: '‚ôªÔ∏è' },
    { value: 'paper', label: 'Papel', price: 0.8, icon: 'üìÑ' },
    { value: 'glass', label: 'Vidro', price: 0.5, icon: 'ü´ô' },
    { value: 'metal', label: 'Metal', price: 2.0, icon: 'ü•´' },
    { value: 'electronics', label: 'Eletr√¥nicos', price: 5.0, icon: 'üì±' },
  ];

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    const items = getRecyclingItems();
    const allUsers = getUsers();
    setRecyclingItems(items);
    setUsers(allUsers);
  };

  const filteredItems = recyclingItems.filter(item => {
    if (filter === 'all') return true;
    return item.status === filter;
  });

  const handleApprove = async (item: RecyclingItem) => {
    setIsProcessing(prev => ({ ...prev, [item.id]: true }));

    try {
      // Update recycling item status
      updateRecyclingItem(item.id, { status: 'approved' });

      // Find and update user credits
      const user = users.find(u => u.id === item.userId);
      if (user) {
        const updatedUser = {
          ...user,
          credits: user.credits + item.estimatedValue,
          totalEarned: user.totalEarned + item.estimatedValue,
        };
        saveUser(updatedUser);

        // If the approved user is the current logged user, update the context too
        if (currentUser && currentUser.id === item.userId) {
          updateUser(updatedUser);
        }
      }

      loadData();
    } catch (error) {
      console.error('Error approving item:', error);
    } finally {
      setIsProcessing(prev => ({ ...prev, [item.id]: false }));
    }
  };

  const handleReject = async (item: RecyclingItem) => {
    setIsProcessing(prev => ({ ...prev, [item.id]: true }));

    try {
      updateRecyclingItem(item.id, { status: 'rejected' });
      loadData();
    } catch (error) {
      console.error('Error rejecting item:', error);
    } finally {
      setIsProcessing(prev => ({ ...prev, [item.id]: false }));
    }
  };

  const getUserById = (userId: string) => {
    return users.find(u => u.id === userId);
  };

  const getMaterialInfo = (type: RecyclingItem['type']) => {
    return materialTypes.find(m => m.value === type);
  };

  const getStatusColor = (status: RecyclingItem['status']) => {
    switch (status) {
      case 'approved': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      default: return 'bg-yellow-100 text-yellow-800';
    }
  };

  const getStatusText = (status: RecyclingItem['status']) => {
    switch (status) {
      case 'approved': return 'Aprovado';
      case 'rejected': return 'Rejeitado';
      default: return 'Pendente';
    }
  };

  const pendingCount = recyclingItems.filter(item => item.status === 'pending').length;
  const approvedCount = recyclingItems.filter(item => item.status === 'approved').length;
  const rejectedCount = recyclingItems.filter(item => item.status === 'rejected').length;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Valida√ß√£o de Reciclagens</h1>
          <p className="text-gray-600 mt-1">
            Interface para coletores aprovarem ou rejeitarem submiss√µes
          </p>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Clock className="h-8 w-8 text-yellow-500" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Pendentes</p>
                <p className="text-2xl font-bold text-gray-900">{pendingCount}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <CheckCircle className="h-8 w-8 text-green-500" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Aprovados</p>
                <p className="text-2xl font-bold text-gray-900">{approvedCount}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <XCircle className="h-8 w-8 text-red-500" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Rejeitados</p>
                <p className="text-2xl font-bold text-gray-900">{rejectedCount}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <UserIcon className="h-8 w-8 text-blue-500" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Total</p>
                <p className="text-2xl font-bold text-gray-900">{recyclingItems.length}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex flex-wrap gap-2">
            {[
              { value: 'pending', label: 'Pendentes', count: pendingCount },
              { value: 'approved', label: 'Aprovados', count: approvedCount },
              { value: 'rejected', label: 'Rejeitados', count: rejectedCount },
              { value: 'all', label: 'Todos', count: recyclingItems.length },
            ].map((filterOption) => (
              <button
                key={filterOption.value}
                onClick={() => setFilter(filterOption.value as any)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  filter === filterOption.value
                    ? 'bg-primary-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {filterOption.label} ({filterOption.count})
              </button>
            ))}
          </div>
        </div>

        {/* Items List */}
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          {filteredItems.length === 0 ? (
            <div className="p-8 text-center">
              <p className="text-gray-500">Nenhuma reciclagem encontrada para este filtro.</p>
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {filteredItems.map((item) => {
                const user = getUserById(item.userId);
                const material = getMaterialInfo(item.type);
                const isProcessingItem = isProcessing[item.id];

                return (
                  <div key={item.id} className="p-6 hover:bg-gray-50">
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-4">
                          <span className="text-2xl">{material?.icon}</span>
                          <div>
                            <h3 className="text-lg font-semibold text-gray-900">
                              {material?.label}
                            </h3>
                            <div className="flex items-center space-x-4 text-sm text-gray-600 mt-1">
                              <div className="flex items-center">
                                <Scale className="h-4 w-4 mr-1" />
                                {item.weight} kg
                              </div>
                              <div className="flex items-center">
                                <Package className="h-4 w-4 mr-1" />
                                {item.quantity} unidades
                              </div>
                              <div className="flex items-center">
                                <UserIcon className="h-4 w-4 mr-1" />
                                {user?.name || 'Usu√°rio n√£o encontrado'}
                              </div>
                            </div>
                            <p className="text-sm text-gray-500 mt-1">
                              {new Date(item.date).toLocaleDateString('pt-BR')} √†s {' '}
                              {new Date(item.date).toLocaleTimeString('pt-BR')}
                            </p>
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <div className="text-right">
                          <p className="text-lg font-bold text-gray-900">
                            R$ {item.estimatedValue.toFixed(2)}
                          </p>
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(item.status)}`}>
                            {getStatusText(item.status)}
                          </span>
                        </div>

                        {item.status === 'pending' && (
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleApprove(item)}
                              disabled={isProcessingItem}
                              className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                              {isProcessingItem ? (
                                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                              ) : (
                                <CheckCircle className="h-4 w-4" />
                              )}
                            </button>
                            <button
                              onClick={() => handleReject(item)}
                              disabled={isProcessingItem}
                              className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                              {isProcessingItem ? (
                                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                              ) : (
                                <XCircle className="h-4 w-4" />
                              )}
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ValidationAdmin;

